import 'dart:async';import 'package:flutter/material.dart';import 'package:g_drive/view/home/home_view.dart';class SpleshView extends StatefulWidget {  const SpleshView({super.key});  @override  State<SpleshView> createState() => _SpleshViewState();}class _SpleshViewState extends State<SpleshView> {  @override  void initState() {    // TODO: implement initState    super.initState();    Timer(      Duration(seconds: 5),      () {        Navigator.pushReplacement(          context,          MaterialPageRoute(            builder: (context) => HomeView(),          ),        );      },    );  }  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.black12,      body: Center(        child: Column(          mainAxisAlignment: MainAxisAlignment.spaceEvenly,          children: <Widget>[            Image(              width: 150,              height: 150,              image: NetworkImage(                'https://mailmeteor.com/logos/assets/PNG/Google_Drive_Logo_512px.png',              ),            ),            Row(              mainAxisAlignment: MainAxisAlignment.center,              children: <Widget>[                Text(                  'Google',                  style: TextStyle(                    fontSize: 25,                    fontFamily: 'PlusM',                    color: Colors.white,                  ),                ),                SizedBox(                  width: 5,                ),                Text(                  'Workspace',                  style: TextStyle(                    fontSize: 23,                    fontFamily: 'PlusR',                    color: Colors.white,                  ),                ),              ],            ),          ],        ),      ),    );  }}